INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'7a462750-ee1d-b8fb-5a4f-000000001050', N'Hocus Pocus Technolocus', N'Wetenschapsgoochelaar Marc Govaert trekt langs Vlaamse scholen met zijn show vol natuurkundige experimenten. Maar ook tijdens de Gentse Feesten wil hij bewijzen dat wetenschap alles behalve saai is. Uit zijn koffers vol huis-, tuin- en keukenspullen tovert hij experimenten die je zelf thuis kan doen en die je helpen de natuur te begrijpen. Een familievoorstelling waarbij je van de ene verbazing in de andere valt. Voor ‘curieuzeneuzen’ van 8 tot 88.', CAST(7 AS Decimal(2, 0)), CAST(N'2022-07-21T10:00:00.000' AS DateTime), CAST(N'2022-07-21T12:15:00.000' AS DateTime), N'13dee832-75a0-40a5-a8f0-9fe96984a56b')
