INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'7ea2053b-442e-7882-f541-000000005325', N'Amelie Albrecht - Zwaar Leven', N'Over het leven van de 28-jarige Amelie Albrecht kunnen we kort zijn: niet noemenswaardig. Na acht jaar tekenacademie kon ze nog niet tekenen, jarenlang vioolles leverde haar nog geen vaag gevoel voor muziek op en paardrijden moest ze opgeven omdat ze allergisch werd voor dieren. En dan zwijgen we nog over haar schoolcarrière. Amelie kreeg haar hele leven te horen dat ze nergens zou komen met haar grote mond en neiging om alles in het belachelijke te trekken. Een eigenschap die haar vroeger voortdurend in de problemen bracht, maar haar in 2018 wel de overwinning van Humo’s Comedy Cup opleverde. Sindsdien verdient ze geld door zichzelf te zijn: charmant onverschillig. En toch is het leven er niet bepaald lichter op geworden. Blij moeten zijn omdat je dan toch iets van je leven maakt is ook een opgave. Het afgelopen jaar nam corona het helaas van haar over, maar vanaf nu toont Amelie graag zelf terug aan hoe Zwaar het Leven wel niet kan zijn.', CAST(16 AS Decimal(2, 0)), CAST(N'2022-07-16T20:00:00.000' AS DateTime), CAST(N'2022-07-17T05:59:59.000' AS DateTime), N'ba8fdfa6-23a4-461d-977d-2ec21aa4a103')
