INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'7f5c8db7-bec7-a1a0-68c0-000000004887', N'Back2Aerobics - 90''s beats', N'Zaterdag 16 juli 2022 toveren we het binnenplein van ''Op De Wolken'' om tot een 90s aerobic scene.    Wat hebben we nodig? Een Gentse zomer, 45min toffe beats en vooral JULLIE!    Welkom voor de aerobic sessie om 11u of 16u in de Warandestraat 13 te Gent.    Prijs per sessie: 13 tot 16 jaar = 10€, 16 tot ... jaar = 12€   Trek alvast die basketsloefkes aan en schrijf je in via de website: https://www.opdewolken.be', CAST(12 AS Decimal(2, 0)), CAST(N'2022-07-16T11:00:00.000' AS DateTime), CAST(N'2022-07-16T12:00:00.000' AS DateTime), N'172a0aac-a99a-4fe4-bbe7-f26dfe048468')
