INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'7fc8f67f-916d-4fbd-bb6e-636cc70eaf60', N'Ventus', N'Deze jonge band uit het Gentse was oorspronkelijk een duo. Maar ze gaan vanaf nu met 5 door het leven. Ze wisselen akoestische muziek af met dreamy Indie pop. Gonzalo houdt het warm met zijn lage stem en Emma haalt met de hare de hoge noten. Tijl de gitarist breekt hier en daar door met een solo, Rune opent met de drum terwijl Taron voor de foundation van de band zorgt samen met zijn Bas.  Toch blijft de Ventus vibe nog steeds aanwezig. Met teksten om over na te denken en bedwelmende melodieën.', NULL, NULL, NULL, NULL)
