INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'7fd764fa-4fd4-4532-be6c-000000002388', N'Swing – Circus Ronaldo', N'Swing maakt de scène vrij voor een nieuwe generatie van Circus Ronaldo. Nanosh Ronaldo creëert de sfeer van circus en variété uit de jaren ‘40, samen met een groep jonge circusartiesten.    Verwacht je aan groots cabaretspektakel met kleine knipogen, een vleugje verleiding en onvervalste swingmuziek. De aanstekelijke speelsheid, de heerlijke dynamiek en de blije opgewektheid van Circus Ronaldo’s jonge garde laat je opnieuw verliefd worden op circus zoals je het al lang niet meer zag.', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-21T20:30:00.000' AS DateTime), CAST(N'2022-07-21T22:00:00.000' AS DateTime), N'0b99db5d-f20e-4498-9b0c-371d03923ea5')
