INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'801e3808-71da-4b8b-a686-049887cf4ecc', N'Ten Feet From Stardom', N'"Want wat zou ‘Gimme Shelter’ van de Rolling Stones zijn zonder het vocale vuurwerk van Merry Clayton? En ook ‘The Great Gig in the Sky’ van Pink Floyd zou zonder de messcherpe zang van Clare Torry maar een mager beestje zijn. Bij het grote publiek zijn ze niet gekend, maar geliefd worden ze door miljoenen wereldwijd.  Ook hier in België hebben we van die briljante backingvocalisten rondlopen. Stemmen die al sinds jaar en dag het zout in de pap vormen van optredens en albums van legendarische artiesten, zowel nationaal, als internationaal. Chaka Khan, The Pointer Sisters, Clouseau, Rick Ashley, Cat Stevens, Sister Sledge, Black Box Revelation, Arno;  de lijst is even eindeloos als indrukwekkend. En nu zetten deze vier gouden stemmen eindelijk zichzelf eens in de spotlights: Nina Babet, Monique Harcum, Chantal en Steve Kashala. Begeleid door Bert Gielen en DJ Grazzhoppa op draaitafels brengen ze de hoogtepunten van hun muzikale helden terug tot de naakte essentie."', NULL, NULL, NULL, NULL)
