INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'8379472c-d522-9d94-f8c3-000000004071', N'Mother Mercury: Queen coverband', N'Deze vijf professionele muzikanten proberen zich er niet makkelijk van af te maken door een valse snor op te kleven. Neen, meneer! Ze hebben alles op alles gezet om de harmonieën en energie tot het hoogste niveau te tillen, zodat jij kan genieten van de songs, zoals je ze kent van de legendarische albums. Mother Mercury is geen doordeweekse coverband die toevallig wat Queen hits speelt. Deze tribute act wou een liveshow creëren waarin ze de ‘Queen’ konden neerzetten die iedereen kent van hun iconische liveconcerten. Je hebt Wembley ’86 en Live Aid ’85 gezien, dus je weet dat Queen er alles aan deed om het publiek een onvergetelijke ervaring te geven. Dat zijn grote schoenen om te vullen, vind je niet?  Freddie Mercury is, zonder twijfel, nog steeds de beste showman die er ooit geweest is. Mother Mercury zorgt ervoor dat je het nooit zal vergeten door een overweldige  show op te voeren met die bekende Queen hits!!', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-23T22:30:00.000' AS DateTime), CAST(N'2022-07-23T23:55:00.000' AS DateTime), N'd55555a1-93e1-492d-925a-e98e3c7ebb7e')
