INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'84a31cc3-a5ef-4593-912c-72f176dbe49d', N'Leander & Djalt - Duvel Delta jazzconcerten', N'Leander Vertriest (20) en Djalt Baculalay (18) zijn twee jonge gedreven muzikanten uit Gent die elkaar gevonden hebben in hun gemeenschappelijke passie voor muziek in al haar vormen, tezamen hebben ze in verschillende groepen en bands gespeeld en met verschillende projecten hebben de twee muzikanten op menig podium gestaan in België, Frankrijk, Servië en Cyprus. In een eeuwige reis doorheen jazz, zigeunermuziek en traditionele volksliederen trachten ze steeds een unieke karakteristieke stijl te vinden en voor dit optreden worden ze bijgestaan door twee vrienden en mede-muzikanten met wie ze de afgelopen jaren hebben opgetreden.   Leander Vertriest: accordeon Djalt Baculalay: gitaar, zang Bartas Boots: saxofoon Andreas Beeckman: contrabas', NULL, NULL, NULL, NULL)
