INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'85619736-216d-4a43-8254-3be19a5d5cce', N'De Kleine Prins - Bovenste Knoopje Open', N'Poëtisch muziektheater over opgroeien, rare grote mensen en liefde  Op een nacht stort Anton van Eierpuree met zijn vliegtuig neer in de Sahara. Hij is helemaal alleen in het midden van de grootste woestijn van de wereld. Na zes lange dagen begint hij, oververhit en doodmoe, dingen te zien die er eigenlijk helemaal niet zijn. Dan verschijnt de Kleine Prins. Hij herinnert Anton aan dat wat hij was kwijtgeraakt. Wanneer iets onzichtbaar is, kun je het namelijk snel vergeten.  Bovenste Knoopje Open baseert zich op het geliefde boek van Antoine de Saint-Exupéry. Vanuit hun cabaretachtergrond voegen zij een eigen geluid toe aan dit verhaal. Met hulp van liedjes vertellen zij hun versie van De Kleine Prins. Een voorstelling over grote mensen en hun hebbelijkheden. Over vriendschap. En bovenal, onzichtbaar voor het oog, de liefde.', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
