INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'893a2243-ec13-4519-aecd-6d3462e722aa', N'Djiezes! Orval tasting', N'Vanuit de Sint-michielsbeweging organiseren we opnieuw ons Djiezes! festival tijdens de Gentse Feesten (15-17 juli) in ons huis en tuin in de Lange Violettestraat 277! Niet te missen!  - Vrijdagavond (15/7, 19u30-22u): open Taizé-gebedsavond gevolgd door lekkere, huisgemaakte spaghetti aan studentikoze prijs en Volksspelen.  - Zaterdag (16/7): Gentse Brunch (8u30-11u) boordevol Gentse specialiteiten en streekproducten! Aansluitend vrij toegankelijke aperitiefconcerten (12u-16u) door bekende en minder bekende namen uit de Gentse (studenten) music scene oa. Lieven Tavernier! - Zaterdagavond (16/7, 17u-18u) verzorgen we ook een Gospel Eucharistieviering in de begijnhofkerk met aansluitend een Orval tasting in het huis (18u15).  - Zondag (17/7): Gentse Brunch (8u30-11u) boordevol Gentse specialiteiten! Aansluitend vrij toegankelijke aperitiefconcerten (12u-16u) met oa. Roel Van Bambost!  Brunch tickets (20€ in VVK) zijn te verkrijgen via karel.sintmichiel@gmail.com', NULL, NULL, NULL, NULL)
