INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'8cd69185-b53b-68f8-e862-000000000780', N'Gentse Buitenband - startplaats Desteldonk', N'De Gentse Buitenband is fietsen, wandelen en feesten in de groene gordel rond Gent.  Kies uit routes langs de mooie fietspaden in het landelijke groen dat Gent omringt. Geniet op onze startplaatsen, stuk voor stuk echte feestlocaties met hapjes, drankjes en animatie voor jong en oud. Starten kan doorlopend tussen 8u en 15u.  Voorinschrijvingen: €3, dag zelf: €5. Kinderen (tot 12j): gratis. Meer info en hoe inschrijven, vind je op onze website.   Door vooraf in te schrijven, maak je bovendien kans om een fiets te winnen, geschonken door BIKER Fietsen.  Hou zeker ook onze facebookpagina in de gaten voor de laatste nieuwtjes. 8km: Koppelwandeling 25km: Langerdonkroute 45km: Veerpontroute 80km: Gentse Buitenband (voor wielertoeristen) Maaltijd (12-14u): Beenhesp met frietjes  Meer info: 0494 54 67 64', CAST(5 AS Decimal(2, 0)), CAST(N'2022-07-17T08:00:00.000' AS DateTime), CAST(N'2022-07-17T15:00:00.000' AS DateTime), N'b3c3c83c-02ce-4822-bb4b-bc2a0bf4d9d5')
