INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'8d4e8bc8-ba9d-49e8-81fa-0db302ca3de5', N'48ste Rondgang van de Stroppendragers en Keizer Karel', N'Gentse Feesten, dat is traditie en geschiedenis. Op 3 mei 1540 vernederde Keizer Karel V de notabelen van Gent - zijn geboortestad - omdat de Arteveldestad zich tijdens de Gentse revolte schuldig had gemaakt aan majesteitsschennis. Hij liet de leiders van de opstand arresteren. Vijfentwintig van hen werden omgebracht, de anderen liet hij bij wijze van bestraffing door de stad gaan met een strop om de hals. Deze strop is blijven leven als het Gentse symbool van fiere weerstand tegen elke vorm van tirannie en misplaatste autoriteit. Dit gebeuren wordt jaarlijks herdacht tijdens de rondgang van de Stroppendragers en Keizer Karel. De stoet start aan het Gravensteen, waar hij ook terug toekomt na een rondgang langs o.m. de Kraanlei, de Vrijdagmarkt, de Belfortstraat, het Emile Braunplein, Klein Turkije en de Groentenmarkt. Applaus voor de stroppendragers en een Keizer Karel die uitgejouwd wordt.', NULL, NULL, NULL, NULL)
