INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'92e7b616-d286-5af9-a73a-000000000771', N'Gentse feestenrevue', N'‘Eindelijnge taad veur de Fieste!’ is een familierevue in het Gents, een bonte mengeling van humor en muziek, met de tijd als rode draad. De sketches hebben allemaal een onderwerp dat iets met tijd te maken heeft, zoals haast hebben, te laat komen, horloges en kalenders, toekomst en verleden … Ook bij de keuze van de muziek (een mix van gekende meezingers en een paar nieuwe zelfgeschreven liedjes) is gezocht naar nummers die tijdsbegrippen als onderwerp hebben. We werken in onze revue steeds rond een thema en die rode draad is niet alleen in de show terug te vinden maar ook in de aankleding van de zaal.  Door de positieve reacties van vorig jaar hoe onze tafelschikking eruit zag t.g.v. de coronamaatregelen hebben we besloten deze schikking min of meer aan te houden. Gezelligheid troef!', CAST(12 AS Decimal(2, 0)), CAST(N'2022-07-22T19:30:00.000' AS DateTime), CAST(N'2022-07-22T22:15:00.000' AS DateTime), N'da5f4fce-b629-46f7-a698-535aa9e03944')
