INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'99bd7d01-4eb0-4fad-8893-77d7a39048dc', N'Gemoedstraminer - Frank Boddin', N'Zanger en cabaretier Frank Boddin ken je ongetwijfeld van zijn debuutplaat BODDIN, met singles zoals Dit is het huis en Bomen. In GEMOEDSTRAMINER bezingt hij nu de gespleten en gefragmenteerde mens, op zoek naar connectie, met veel melodie, pakkende ritmes en scherpe teksten. Frank Boddin (1983) groeit op als zoon van een pianostemmer en leert zichzelf piano door liedjes van de radio na te spelen. Zijn vader sleurt hem jaarlijks mee naar de Brussels Jazz Marathon waar hij als 10-jarig ketje naar Keith Jarrett, Kenny Werner en Toots zat te luisteren. 8 jaar later wordt Frank Communicatiewetenschapper aan de VUB, maar de artistieke kriebel blijft en nog eens 8 jaar later sticht hij cabaretduo Kunst Veredeld Met Een D en volgt hij als eerste Vlaming een cabaretopleiding in Nederland.', NULL, NULL, NULL, NULL)
