INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'9da059cd-e4a8-4b17-a5ba-117001e88cc7', N'we are all time travellers', N'Kom even uitblazen op de mooiste stilte plek van Gent: de Campo Santo heuvel, het Pére Lachaise van Vlaanderen. In de exporuimte op de top (Sint Amanduskapel) brengt Boanerges een verwonderende verzameling portretten, objecten en monumentale schilderwerken over identiteit en tijd, onder de noemer ''we are all time travellers''. De werken staan opgesteld in het verlichte, feeërieke decor van de kapel en de omliggende heuvel.     Het werk van Boanerges vormt een onderzoek naar de impact en kracht van tijd op onze levensloop.  Maak een afspraak met de kunstenaar voor een privé-toelichting. Groepen of geïnteresseerde kopers ook buiten de openingsuren. 0475 65 99 59', NULL, NULL, NULL, NULL)
