INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'a099a6b9-35c5-efa4-ff37-000000003216', N'Frimout', N'In 2012 begint de Nederlandstalige band Frimout rond frontman Stef Willems aan hun missie: met eigen Nederlandstalige popmuziek de Vlaamse muziekwereld veroveren.  Hun swingende popsongs en bloedmooie persoonlijke ballades worden warm onthaald door de radio’s. 17 zelfgeschreven songs schoppen het tot in de playlist van Radio2, de grootste radiozender van Vlaanderen. De liefdessong ‘Ninett’ uit 2014 is intussen uitgegroeid tot een instant klassieker. Maar ook de singles ‘Sterren Zien’ en ‘Toffer dan tof’ worden grijsgedraaid. Topproducer Steve Willaert (oa Natalia en Will Tura) zet in de studio volop zijn schouders mee onder de band.  Na een pauze van 1,5 jaar slaat Frimout begin 2021 een nieuwe richting uit. ‘Eenzame Reiziger’, een samenwerking met de Amerikaanse flamencogitarist El Javi, laat in april 2021 een nieuw geluid horen en scoort hoog in de Vlaamse hitlijsten. Begin maart 2022 verschijnt hun nieuwe single ‘De overkant’, .', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-18T19:45:00.000' AS DateTime), CAST(N'2022-07-18T21:15:00.000' AS DateTime), N'cf874cde-7c20-4b16-bb69-b1146ba01057')
