INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'a0ea06d0-c4fc-0643-f61a-000000002397', N'Umwelt – Compagnie Endogène', N'Na 2 weken creatieresidentie kan je sneakpeak ervaren van de spiksplinternieuwe creatie ‘Umwelt’. Een voorstelling die in het najaar 2022 in première zal gaan.     Een menselijk wezen gaat al dansend in dialoog met tijd, oorsprong, dierlijkheid en menselijkheid. Een dans van metamorfose. Zoekend naar het beestachtige zelf, zoekende naar alle stukjes van zijn eigenheid - een reis in de genetische geschiedenis die ons heeft gebracht waar we nu staan als mensheid.     ‘Ik stond oog in oog met mijn discipline en met mijn trouwe handlangers, de jongleerballen. Na jaren van passie, praktijk en discipline volgden de twijfel, afkeer en gedachten aan stoppen. Maar oude liefde roest niet. De jongleerballen lieten me niet los en ik vond opnieuw de goesting en motivatie. De ideale aanleiding voor deze solocreatie over een intieme relatie. Of eerder een inwendige dialoog waarin lichaam en objecten de vectoren zijn’  - Morgan Cosquer', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-15T18:00:00.000' AS DateTime), CAST(N'2022-07-15T18:30:00.000' AS DateTime), N'56a218ad-2d30-4737-9eec-19d2f255eb57')
