INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'a139fee6-7bed-69df-4c3c-000000002469', N'Rondleiding: De Coupure - platteland wordt residentieel wonen', N'Tot het interbellum werden professoren en studenten gerecruteerd uit de welgestelde burgerlijke elite. Wanneer ze in Gent kwamen wonen, kozen Gentse professoren bijgevolg ook voor de wijken van deze elite. In de buurt van de Bijloke en de Coupure bevond zich een dergelijke concentratie.  De Coupure was nochtans al eerder gegraven, namelijk rond 1750, tussen de Brugse Vaart en de Leie om een nieuwe impuls te geven aan de ontluikende industrie in Gent. Het Sint-Agnetesas is er nog een getuige van. Door de aanleg van de haven in het noorden van de stad, langs het kanaal Gent – Terneuzen, verliet de industrie de Coupure en werd het kanaal, dankzij zijn landelijke karakter aanleiding tot een groene promenade, waar wie voldoende middelen had zich graag kwam vestigen. Er kwamen aanvankelijk buitenverblijven en herbergen, waar men de rust opzocht. Hier ook stond de wieg van de Gentse floraliën en vestigden zich klinieken en kloosters.', CAST(10 AS Decimal(2, 0)), CAST(N'2022-07-16T14:00:00.000' AS DateTime), CAST(N'2022-07-16T16:00:00.000' AS DateTime), N'56837063-8cf2-4f6e-8caa-12bbd9e537d2')
