INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'a16f6412-939d-3782-93da-000000000762', N'"Rondvaart ""Gent Compleet met sluizen"" + gratis ijsje. Duur: 90 minuten."', N'Gent is terug een eiland. De vaarcirkel is rond. Zeker verrassingen: 2 versassingen nl. Reep en Baudelokaai! Fijne verkenning van Gent met gidsing in het Nederlands. 1000 jaar architectuur en verhalen. Tickets kopen: online of live afhalen tot 15 min. vóór afvaart.', CAST(14 AS Decimal(2, 0)), CAST(N'2022-07-22T14:00:00.000' AS DateTime), CAST(N'2022-07-22T15:30:00.000' AS DateTime), N'47a30efc-6bad-422b-9fd3-7b22e5c3d256')
