INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'a222c6c5-025b-4cbe-bd02-16cb04aeeec1', N'Expo Over mensen en machines', N'Twaalf mannen en vrouwen, geboren tussen 1660 en 1965, nemen je mee in een universeel verhaal over mensen en machines en hoe die de wereld rondom ons veranderden. Ga op verkenning in vijf ontdekkingsboxen vol historische filmpjes, indrukwekkende machines en verrassende weetjes.', NULL, NULL, NULL, NULL)
