INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'a55a648e-d1ce-a731-0ba7-000000003081', N'Familierondleiding expo Kassa kassa!', N'Welkom in de expo Kassa kassa! Zet je samen met je kinderen schrap voor een nostalgische trip langs de kruidenier om de hoek, vintage reclameborden, een ouderwetse melkkar … Maak op een boeiende, interactieve manier kennis met hedendaags koopgedrag, het fenomeen massaconsumptie en onze wegwerpcultuur.  Tijdens deze rondleiding op maat van families, loodst een gids groot en klein doorheen het winkellandschap van vroeger en nu. Onderweg zie je objecten en hoor je geluiden uit de verrassend onbekende wereld die zich afspeelt achter de vertrouwde vitrine.  Ontdek samen hoe de buurtwinkel uitgroeide tot supermarkt, welke klanken op de markt weerklinken en door wiens handen jouw pakjes allemaal gaan. Wedden dat jullie voortaan helemaal anders naar de producten in de winkelkar of het (online) winkelmandje kijken?', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-16T14:00:00.000' AS DateTime), CAST(N'2022-07-16T15:30:00.000' AS DateTime), N'575c63fb-8db5-48f7-a66b-4ebf8a48b6d3')
