INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'a5dd6255-b88e-4943-ae6e-cba2005bca92', N'"""Culinaire èkspozee"" deur Perfesser Gensch Peter Van Haelter"', N'Dat het Gents dialect deel uitmaakt van ons cultureel erfgoed is geen verrassing.  De kleur, de kracht, de schilderachtigheid en de pittigheid van het Gents dialect vormen dan ook de volksgeest en de ziel van het Gentse volk. Alleen al daarom dienen we het Gents dialect in ere en in stand houden.  Speciaal voor de Gentse Feesten 2022 maakte de Perfesser Gensch een nieuwe “èkspozee” en deze keer in het thema “Culinair Gents”. De Perfesser Gensch trakteert u niet alleen op een interactief thematisch overzicht van de vele woorden en uitdrukkingen die ons Gents dialect rijk is, maar verwent u ook met enkele typische Gentse proevertjes en sneukelingen.   Deze voorstelling wordt u gepresenteerd in de foyer van de Minard en start om 11u00.  Het juiste uur en de juiste plaats om van een aperitiefvoorstelling te kunnen spreken !', NULL, NULL, NULL, NULL)
