INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'ab15f966-6ac5-23ec-393c-000000004173', N'Op de boerderij /klassiek voor kleintjes - Krulmuziek', N'Een muzikaal feestje voor peuters en kleuters die van gezelligheid en muziek houden! Sebastiaan en Félice van Krulmuziek spelen fijne klassieke muziek voor jonge kinderen. Samen met de cello, harp en fluit hobbelen de kinderen mee met het paard, knorren als het biggetje en mekkeren als echte muzikale schapen op muziek van Bach, Mozart en Schumann. De eendjes doen hun zwemdansje op klassieke tonen, tot de gans in het water plonst… Kinderen kunnen hun eigen boerderijdierenverzameling van thuis meenemen naar het concert. Deze boerderijdieren wandelen, spelen en reageren op de vrolijke muziek. Ie-aa-ie-aa-oh!', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-17T10:00:00.000' AS DateTime), CAST(N'2022-07-17T10:45:00.000' AS DateTime), N'06b68e9f-60a3-4870-a89f-fddcf82fe219')
