INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'ad6c9555-8679-4852-9bb3-93edfaaa113b', N'Ann Lee', N'"Ann Lee brengt een unieke mix van blues, jazz, country en rockabilly.  Hun muziek is een kruisbestuiving tussen donkere ""Tarantineske"" nummers, gekleurd door een melancholische trompet en meer lichtvoetige country en rockabilly. De jazz en blues-invloeden zijn nooit ver weg, maar altijd vanuit een originele invalshoek - nooit voorspelbaar, steeds verrassend en uitdagend en altijd vol passie en overtuiging.  In 2020 brachten ze hun eerste EP ''Down the road'' uit, en ze staan te popelen om hun nieuwe werk aan de wereld te laten horen.  Ann Lee = Annelies Vandenabeele - zang & akoestische gitaar . Hélène Deklerck - drum . Bart Parmentier - contrabas . Wesley Vandewalle - trompet . Maarten Biesbrouck - gitaar"', NULL, NULL, NULL, NULL)
