INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'ae563bd5-285e-4044-af8c-195c0954ffa9', N'’t Zwyn deur de Bjèten – een Stomende cursus West-Vlaams', N'Karel Declercq speelde jarenlang het typetje Marcel Verachtert op radio 2 (‘Zij mee ?’) met als tegenspeler Gust De Coster en Sabientje ‘myn appelsientje’ Devos. Ook op de Vrt leerde hij als ‘professor Declercq’ de Limburger Peter Hermans West-Vlaams. Het onvermijdelijke is gebeurd: Karel heeft zijn lessen gebundeld tot een live-programma onder de titel ''t Zwyn deur de bjèten'' (wat betekent: eens deftig de bloemetjes buiten zetten).  Het is een show in een absurde stijl, waar het publiek sterk bij betrokken wordt. Nog nooit zult u zoveel gelachen en geleerd hebben tegelijk!  De voorstelling vindt plaats in open lucht, op het gezellige binnenplein van het François Laurentinstituut. Het plein is gedeeltelijk overdekt.', NULL, NULL, NULL, NULL)
