INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b10c2ad1-0496-777d-c769-000000005235', N'JAMAN! (live)', N'Jaman!, een Gentse power reggae band opgericht in 1997, is zonder twijfel de beste en meest gedreven reggaeband van België. Jaman! staat voor muzikale kwaliteit, trouw aan de roots, toch creatief en opwindend, en vooral een goede live act. Op het podium brengen ze een spectaculaire mix van gestroomlijnde reggae, goede dubs, old school toasting, mooie samenzang en feestelijke deuntjes.   Ze verrassen het publiek met onverwachte breaks en bekende songfragmenten, zang en rap vlot afwisselend. Covers speelt de groep nauwelijks, en ook dat is een verrassing.  Maar het mooiste is misschien nog dat ze alle groengeelrode grenzen doorbreken. Live levert dat een kleurrijk en gevarieerd spektakel op, een stevige fond van reggae, gelardeerd met pop en hiphop, zoals die de afgelopen jaren duizenden festivalgangers in België en Nederland bekoord heeft.  Ter gelegenheid van hun 25-jarig bestaan maken ze een comeback in 2022 waar zowel oude als nieuwe songs aan bod komen.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-22T20:30:00.000' AS DateTime), CAST(N'2022-07-22T22:00:00.000' AS DateTime), N'a0ce02e6-c5ad-44de-8e44-ebda43f17bcb')
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b10de093-3c90-305c-518c-000000001608', N'N. Dash', N'De werken van N. Dash ontstaan vanuit een intense en gelijkwaardige uitwisseling met wat nabij is. In haar veelzijdige praktijk verwerkt de kunstenaar nederige materialen zoals vuil, aarde, jute, touw, reepjes piepschuim, plastic flessen en karton tot complexe composities. Ze ontleent daarbij methodes en substanties aan de schilderkunst, beeldhouwkunst, fotografie en drukkunst. Via deze transformaties lijkt er zich een paradox te vormen bij de beleving van de werken, die zowel verfijnd als ongecompliceerd aandoen. De vitaliteit en zuinigheid van de constructies zijn te herleiden tot haar verbondenheid met de omgeving, zowel natuurlijk als geconstrueerd, en met de geologische tijd of het enorme interval waarin biologisch leven op aarde is ontstaan. Het tactiele werk vertoont een onderliggende spanning tussen wat industrieel ‘gegeven’ en wat manueel gemaakt is, tussen de onvoorspelbaarheid van de elementen en de menselijke pogingen om deze natuurlijke energieën te beheersen.', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-21T10:00:00.000' AS DateTime), CAST(N'2022-07-21T18:00:00.000' AS DateTime), N'65a6c4fa-bdd3-4351-b10d-aa50d70ec5d2')
