INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b112168d-54ce-405f-9fc4-58269d8b45c5', N'Zoektochtboekje: Neuzen in historisch Gent', N'Welke torens vormen de bekende Torenrij? Wat stellen ‘de Gekroonde Hoofden’ voor? Wanneer werd het Gravensteen gebouwd? Hoeveel weegt de kanonskogel van de Dulle Griet? In welk straatje ruikt het altijd naar verf? Hoe gaat de legende van de Hoofdbrug? Dit alles en nog véél meer komt u te weten tijdens deze zoektocht.  Kostprijs: € 10/boekje.  Een zoektochtboekje kan gedeeld worden door 3 à 4 personen.  Boekjes zijn te koop op de Botermarkt 9, 9000 Gent (dinsdag tot zondag tussen 10u00 en 18u00). Op maandag enkel via afspraak info@gandante.be.', NULL, NULL, NULL, N'62326b09-ddeb-4795-aee1-99ad45e2d07f')
