INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b191a36c-ff8b-4799-bfa4-000000000786', N'Gentse Buitenband - startplaats Drongen', N'Fietsen, wandelen en feesten in de Groene zone rond Gent.  Kies uit routes langs de mooie fietspaden in het landelijke groen dat Gent omringt. Geniet op onze startplaatsen, stuk voor stuk echte feestlocaties met hapjes, drankjes en animatie voor jong en oud.  Starten kan doorlopend tussen 8u en 15u.  Voorinschrijvingen: €3, dag zelf: €5. Kinderen (tot 12j): gratis.   Meer info en hoe inschrijven, vind je op de website buitenband.be.   Door vooraf in te schrijven, maak je bovendien kans om een fiets te winnen, geschonken door BIKER Fietsen.  Hou zeker ook onze facebookpagina in de gaten voor de laatste nieuwtjes.  8km: Onumentroute (wandeling) 25km: Korte Keten route 45km: Gentse Kweek tussen Leie en Schelde 80 km: Gentse Buitenband (voor wielertoeristen) Maaltijd (12-14u): Beenhesp met frietjes Meer info: 0476 61 69 54', CAST(5 AS Decimal(2, 0)), CAST(N'2022-07-24T08:00:00.000' AS DateTime), CAST(N'2022-07-24T15:00:00.000' AS DateTime), N'5113c25d-e03c-44f4-8430-38c7884c7186')
