INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b193c0b3-aac4-3443-8475-000000003021', N'St. Franciscuswandeling De Kuip', N'Sinds de eerste Minderbroeders rond 1225 in Gent aankwamen, is er veel gebeurd. Kloosters kwamen en gingen, maar de spiritualiteit van Sint-Franciscus van Assisi bleef overeind in de Arteveldestad, al gebeurt het vandaag eerder in de vorm van aandacht voor de kansarme of de dakloze. Ook de interreligieuze dialoog vandaag gaat terug tot op St. Franciscus. We ontdekken de plaatsen van de Franciscaanse spiritualiteit tussen Leie en Schelde in de voetsporen van Sint-Franciscus. Poverello, Parnassus, de nog actieve Sint-Antoniuskring, het Arme Klaren-klooster in de Rabotstraat, Pedro de Gante, ...  Gentse spiritualiteit door een positieve Roze Bril.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-15T14:30:00.000' AS DateTime), CAST(N'2022-07-15T16:30:00.000' AS DateTime), N'0fa2a4e1-2a7e-49d6-b9d2-0ed2ca0e54a0')
