INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b287f346-c68c-4cae-ed43-000000005355', N'Lunchconcert Lami Trio', N'Lunchconcert ten voordele van de tewerkstellingsprojecten van maatwerkbedrijf Ateljee vzw.  Concert: Lami Trio. Twee snaarinstrumenten (een qanun en een cello) en een stem die balanceert tussen Oost & West. Samen zorgen ze voor een bad van melodieën, klankkleuren en ritmes, van lyrische passages over felle uithalen naar pure emotie.  Osama Abdulrasol, Lode Vercampt en Helloune (Helena Schoeters) brengen met Lami Trio een magische mix van klanken die muziekstijlen van over de hele wereld met elkaar verbindt.  Op het menu: Aperitief met een variëteit van tapenades en crostini Voorgerecht: bisque van garnaal met toast en rouille  Hoofdgerecht: Marokkaanse tajine met hoevekip en merguez Dessert: trifle van abrikozen en mascarpone met amaretti, pistachenoten en limoen', CAST(40 AS Decimal(2, 0)), CAST(N'2022-07-20T12:00:00.000' AS DateTime), CAST(N'2022-07-21T05:59:59.000' AS DateTime), N'e96048a4-0124-4276-aa5b-396c67e7701d')
