INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b2a13b41-3581-47ee-a7ef-f01cbc9dd242', N'"Magic Show ""Magic Michael:Prunella in Hollywood Magic"""', N'Magic Michael & Piccolo nemen jullie tijdens de Gentse Feesten mee in de wereld van Hollywood vol magie en illusie.   Tante Prunella zet Magic Michael voor een grote keuze in deze voorstelling. Kiezen tussen zijn beste vriend Piccolo of een show spelen in Hollywood. Op voor waarde dat hij deze week nog zal trouwen met zijn assistente.    Wanneer blijkt dat de assistente op wereldreis is vertrokken vallen alle plannen in het water voor hem. Bye Bye Hollywood of heeft Piccolo nog een Piccolonies idee om van de tante af te raken en zijn vriendschap te redden ?    Kom het te weten in deze super toffe Gentse Feesten show.   Koop nu snel je tickets.', NULL, NULL, NULL, NULL)
