INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b2ae9471-5f62-45c8-979b-000000002472', N'Familienamiddagen', N'Vanaf 14u is iedereen welkom op het festivalhart, in het Coyendanspark. Hier worden je namiddagen gevuld met speelplezier voor grote en kleine kinderen. Zwemmen in het ballenbad, springen op de grote springballen of zandkastelen bouwen. Plezier voor jong en oud gegarandeerd tijdens ons festival.   Club Tropicana   Een stomende private pool party! Breng je badpak mee voor een heerlijke duik. Kom alvast in de juiste sfeer met de theme-song van deze caravan en bestel een lekkere cocktail.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-16T14:00:00.000' AS DateTime), CAST(N'2022-07-17T00:00:00.000' AS DateTime), N'49cf6ded-37c9-4c19-b42d-90792660a9a9')
