INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b2ff8d1d-4f03-daf5-9b60-000000003240', N'Expo 100 % Textiel', N'Laat je meevoeren naar de spinnerij, weverij, ververij en het confectieatelier. Ontdek de zoemende spinmachines, bulderende weefgetouwen en ratelende naaimachines. Neem een duik in kleurrijke stalenboeken en fascinerende verhalen. Welkom in de wondere wereld van textiel!', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-20T10:00:00.000' AS DateTime), CAST(N'2022-07-20T18:00:00.000' AS DateTime), N'3d2951a8-9402-4d5c-8ee2-4a7399e43ec2')
