INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b3a77f07-4624-2900-375f-000000002406', N'Rondleiding: Anseele? Socialist van het eerste uur zet Gent op de kaart!', N'Zonder Edward Anseele zou Gent, het socialisme en België er anders uitgezien hebben. Deze tijdsgenoot van Daens heeft véél meer betekend voor de ontvoogding van de arbeider dan zijn katholieke tegenpool. De strijd tegen het establishment (nu “the swamp” genoemd) was puur pionierswerk en kreeg bewondering en navolging in Europa. Volg Anseele op een wandeling door zijn leven in Gent en sta versteld van zijn strijd en verwezenlijkingen.', CAST(10 AS Decimal(2, 0)), CAST(N'2022-07-15T10:00:00.000' AS DateTime), CAST(N'2022-07-15T12:00:00.000' AS DateTime), N'b763bd8f-37cb-4137-b514-3414a8a74fc3')
