INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b66c7d2a-1e46-8ac3-a27d-000000001608', N'N. Dash', N'De werken van N. Dash ontstaan vanuit een intense en gelijkwaardige uitwisseling met wat nabij is. In haar veelzijdige praktijk verwerkt de kunstenaar nederige materialen zoals vuil, aarde, jute, touw, reepjes piepschuim, plastic flessen en karton tot complexe composities. Ze ontleent daarbij methodes en substanties aan de schilderkunst, beeldhouwkunst, fotografie en drukkunst. Via deze transformaties lijkt er zich een paradox te vormen bij de beleving van de werken, die zowel verfijnd als ongecompliceerd aandoen. De vitaliteit en zuinigheid van de constructies zijn te herleiden tot haar verbondenheid met de omgeving, zowel natuurlijk als geconstrueerd, en met de geologische tijd of het enorme interval waarin biologisch leven op aarde is ontstaan. Het tactiele werk vertoont een onderliggende spanning tussen wat industrieel ‘gegeven’ en wat manueel gemaakt is, tussen de onvoorspelbaarheid van de elementen en de menselijke pogingen om deze natuurlijke energieën te beheersen.', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-23T10:00:00.000' AS DateTime), CAST(N'2022-07-23T18:00:00.000' AS DateTime), N'65a6c4fa-bdd3-4351-b10d-aa50d70ec5d2')
GO
INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'b67c3325-1667-c99a-6be9-000000003462', N'CodeFever', N'Ontdek hoe cool programmeren is met CodeFever!    Droom je ervan je eigen game te maken? Of zou je graag de geheimtaal van je pc kunnen kraken?  Met CodeFever duik je écht achter de schermen. En leer je hoe computers denken en werken!  Is coderen wel iets voor jou? Wil je het graag eerst even proberen? Dat kan!  We geven jou de kans om eens lekker te komen experimenteren.  We dompelen je onder in een virtueel bad van programmeren, introduceren je in de programmeerwereld van Scratch en al snel heb je je eerste spelletje geprogrammeerd…  Een ideaal voorproefje voor onze echte lesreeks!  Plezant en interessant, nu en later! Want misschien word jij wel robotbestuurder, drone ontwikkelaar of verzin je zelf een zotte toekomstjob!  WARNING!  Het codevirus is zeer aanstekelijk! De kans is zeer groot dat je de smaak erna levenslang te pakken hebt …', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-24T13:00:00.000' AS DateTime), CAST(N'2022-07-24T19:00:00.000' AS DateTime), N'277773e5-cc8f-442a-8aed-a187f41b8df1')
