INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'bbb1a30a-f070-f03e-526a-000000005331', N'The A-ties (met Kurt Burgelman)', N'Iedereen die de jaren ’80 heeft meegemaakt, kijkt met nostalgie terug naar een decennium van hoop en schitterende muziek. Fluo aerobic-outfits en (gescheurde) jeans vol buttons bepaalden het straatbeeld. New wavers in lange grijze jassen en gegespte puntschoenen föhnden hun haar bij dezelfde kappers als de hairband-rockers.  De hits van Prince, Madonna, Depeche Mode, Duran Duran knalden in toen nog niet zo gelimiteerde decibels uit de boxen. In de jeugdhuizen draaiden we The Clash, en Nik Kershaw… we slowden voor het eerst op Frankie goes to Hollywood en headbangden met Bon Jovi en Van Halen.     The A-ties neemt je weer mee naar deze prachtige tijd.  Leg dus je rubiks kubus langs de kant, haal je zweetbandjes en Millet-jas uit je zolderkast, trek je Dr. Martins aan, spring op je BMX en reis met ons back to The A-ties!', CAST(16 AS Decimal(2, 0)), CAST(N'2022-07-23T20:00:00.000' AS DateTime), CAST(N'2022-07-24T05:59:59.000' AS DateTime), N'c3ec4cab-5426-4bc7-869c-b86b6d996847')
