INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'bbed2bec-2033-4024-a9ac-2424a9097975', N'Splendid Isolation', N'"Splendid Isolation onderzoekt hoe kunstenaars creatief zijn of zijn geweest in periodes van afzondering. Zelfgekozen isolement kan een voordeel zijn of zelfs een noodzakelijke voorwaarde voor de praktijk van sommige kunstenaars, maar voor veel kunstenaars is isolement een conditie die wordt bepaald door geografische, politieke of biologische factoren. Wat de oorzaak van het isolement ook mag zijn, veel kunstenaars grijpen deze situatie aan om werk te maken;  dit uit noodzaak, als uitweg, als aanklacht of als getuigenis.  Splendid Isolation brengt verschillende verhalen en ensembles samen van kunstenaars over de hele wereld die werken of gewerkt hebben in afzondering."', NULL, NULL, NULL, NULL)
