INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'bc3e8072-f993-4a02-ace8-223bff405d48', N'PLAYBACK THEATRE (Cut.Paste.Rewind.Vergroot.) door FUS! ( Alles kapot festival)', N'Playback Theatre? Dat is terugspeeltheater. Daarbij gaat het over het terugspelen op basis van jouw autobiografische verhalen. Acteurs zetten met humor, gevoel, respect en fantasie een verhaal, een thema of een gevoel in een ander licht. We bevragen, prikkelen en maken voelbaar wat er leeft onder het publiek. Terugspeeltheater verheldert en verbindt. En u drukt op PLAY!  De acteurs van FUS! zijn ervaren dramatherapeuten, improvisatoren en vooral humorvolle empathische mensen met een luisterend oor voor uw verhaal.  (www.fusimprov.be)', NULL, NULL, NULL, NULL)
