INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'bfda8a4a-5150-380e-9cad-000000002757', N'BTZ (Beatz)', N'De partyband “BTZ” (lees: beats) brengt de beste hedendaagse hits op hun eigen manier.  Ze kneden dansbare muziek van onder meer Dua Lipa, Daft Punk, Major Lazer, Stromae en Netsky tot een knallende liveshow.  Maar “BTZ” is meer dan een doorsnee coverband: het is live-muziek én een DJ-set in één. Naast twee charismatische topzangeressen,  Silke Mastbooms en Nona Dekeyser, heeft “BTZ” ook een DJ in huis: Sonny Vande Putte. Die heeft zijn strepen verdiend als zanger bij partyband The Vicious, en kruipt nu in de rol van “MC” en bedient de samples. Gooi daar nog een pianist en een drummer bij, en het dak gaat er ongetwijfeld af.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-15T20:00:00.000' AS DateTime), CAST(N'2022-07-15T21:30:00.000' AS DateTime), N'54029461-9794-4049-8c3d-7e10f78a1b53')
