INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'c0aac33b-6f23-0b84-3adb-000000002457', N'Clan Cabane – La Contrebande', N'Een bevriende groep acrobaten vindt hun innerlijk kind terug en herontdekken het plezier dat hun samenbrengt. Samen bouwen ze een eigen wereldje, als een kamp vol verhalen, spel en avontuur.    In 2018 zagen we hoogvliegende stunts van Bal Trap. Eindelijk komt deze knotsgekke bende terug voor nog meer ongecompliceerd, spectaculair, acrobatisch circus.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-19T20:30:00.000' AS DateTime), CAST(N'2022-07-19T21:20:00.000' AS DateTime), N'b5d76ad5-4805-42e4-a5f3-a527de726693')
