INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'c31c9f27-60b9-9802-729b-000000002271', N'Zoektochtboekje: Sherlock Holmes in het Patershol', N'Een zoektocht voor het hele gezin in één van Gents oudste en gezelligste wijken. Aan de hand van een leidraad, vragen, raadsels en puzzels leer je het Patershol op eigen tempo beter kennen.  Deze zoektocht bestaat ook in het Frans.  Kostprijs: € 10/boekje.  Een zoektochtboekje kan gedeeld worden door 3 à 4 personen.  Boekjes zijn te koop op de Botermarkt 9, 9000 Gent (dinsdag tot zondag tussen 10u00 en 18u00). Op maandag enkel via afspraak info@gandante.be.', CAST(0 AS Decimal(2, 0)), CAST(N'2022-07-23T10:00:00.000' AS DateTime), CAST(N'2022-07-23T18:00:00.000' AS DateTime), N'98cde59f-4a52-420c-8c90-1b00e0b6d991')
