INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'c9685862-4c53-8bc2-0d5d-000000004185', N'De man die zijn eigen huisdier werd - K.RJ Tochthond/Ken Van Roose', N'Rutger Vandierendonck weet het al van toen hij nog maar zes jaar oud was: hij moet en hij zal dierenarts worden, ook al gelooft niemand hem. Maar Rutger volgt zijn dromen en zet door, ondanks alles wat hem overkomt.  Op zijn pad ontmoet hij zijn grote liefde Mauri, zijn beste vriend Johan, de hondentweeling BasEnMax, een opgezette wezel en leert hij het echte leven kennen. K.RJ Tochthond aka Ken Van Roose is schrijver, muzikant en verhalenverteller. In de cabaretvoorstelling ‘De Man Die Zijn Eigen Huisdier Werd’ kruipt hij gedurende een anderhalf uur durende existentiële trip in de huid van Rutger Vandierendonck, een dierenarts vol mensenkennis en ongevraagd advies. Een huiskamervoorstelling voor en over mensen, dieren en het leven zelf.  Spel: K.RJ Tochthond/Ken Van Roose Tekst: Ken Van Roose, Gert-Jan Termote Regie: Walter Janssens', CAST(12 AS Decimal(2, 0)), CAST(N'2022-07-19T17:00:00.000' AS DateTime), CAST(N'2022-07-19T18:30:00.000' AS DateTime), N'15064436-056c-4d47-8ed3-ad5456c935e9')
