INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'ccec1c9f-4b19-cd94-edb3-000000004734', N'De Liedjesatlas - Peer en Stefan', N'Peer en Stefan reizen in deze voorstelling samen met hun jonge publiek de wereld rond en zingen de leukste traditionals van de landen die ze aandoen. Hoe vergaat het de verliefde indiaan Adriaan? Wat maakt de leukste kok van Suriname klaar? Hoe ziet een school in Afrika er uit? Welke dieren wonen er in Indonesië? En wat krijg je in een Spaans restaurant op je bord? Rasentertainer Peer de Graaf en topgitarist Stefan Blankestijn laten kinderen horen en ervaren wat hun leeftijdgenoten in andere landen leuk vinden en met wat voor muziek zij daar groot worden. De Liedjesatlas is een interactieve en levendige muziekvoorstelling met door Koos Meinderts vertaalde liedjes. Een reis door de wereld!', CAST(11 AS Decimal(2, 0)), CAST(N'2022-07-20T10:00:00.000' AS DateTime), CAST(N'2022-07-20T11:00:00.000' AS DateTime), N'035668c8-f610-467e-b530-e76607aed9da')
