INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'd1727ac5-8941-2c71-cdd6-000000002454', N'Sawdust Symphony – Michael Zandl, David Eisele & Kolia Huneck', N'Sawdust Symphony gaat over het menselijk verlangen om te creëren: het proces van visie tot realisatie, de tragedie van werk en de relatie tussen gereedschap en mens. Drie geobsedeerde personages nemen het publiek mee in een unieke ‘doe-het-zelf’ ervaring waarin zij de eigen werkruimte én zichzelf ontdekken en transformeren.       Een intense dialoog tussen voldoening en frustratie met een vleugje benzine.   Sawdust Symphony is een circusvoorstelling waarin Michael Zandl, David Eisele en Kolja Huneck hun passie voor knutselen, vers hout en objectmanipulatie combineren.', CAST(15 AS Decimal(2, 0)), CAST(N'2022-07-18T20:00:00.000' AS DateTime), CAST(N'2022-07-18T21:00:00.000' AS DateTime), N'467e5812-922e-4f4c-bb2b-d7ea33bc75bd')
