INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'd22bf9b7-259d-4069-923c-cb7b3d29b89b', N'Ik heb een heel vies woord geleerd, dat woord dat rijmt op stoep! - Jasper Smit', N'In 2019 én 2021 werd Jasper genomineerd voor de Willem Wilminkprijs voor beste kinderlied. De lat ligt hoog, want het moet echte kleinkunst zijn: eigenzinnige liedjes over belangrijke dingen in een kinderleven. Vaak grappig, soms serieus en goudeerlijk. Kinderen hebben genoeg dingen aan hun hoofd die grote kleinkunstliedjes verdienen. En dus zingt Jasper over iedereen moeten zoenen op verjaardagen. Over doen alsof je slaapt wanneer je vader je ’s nachts uit de auto naar bed draagt. En over moeders die hun chocolade voor je verstoppen. Jasper maakt al ruim twintig jaar liedjes voor volwassenen (winnaar Utrechts Cabaret Festival, finalist Cameretten Festival) en richt nu al zijn energie, blijheid en podiumervaring op een jong publiek. Met een vlijmscherp randje en een enorm hart.  Met de liedjes van Jasper mag je meteen meezingen. En je onthoudt ze de rest van je jeugd. En natuurlijk leer je wat er rijmt op ‘stoep’! Zoals… soep! En snoep! En YouTube! En hoelahoep! Enzo.', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
GO
