INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'd3d4755f-bd9d-41fd-aa3d-c6ec592826e9', N'Reis naar de blues - Guy Verlinde', N'De blues wordt algemeen beschouwd als de basis van de moderne popmuziek. Vanop de katoenvelden in Mississippi waar de Afrikaanse slaven hun “working songs” zongen, over de zwoele “house parties” van Chicago, ontwikkelde deze muziekvorm zich tot de basis van de rock ’n roll, soul, funk, disco, R& B en Hiphop.  Dit is een zeer interactief concert waar de participatie en beleving van de leerlingen centraal staan. Ze worden uitgenodigd om te dansen, te zingen en ze ontdekken verschillende blues ritmes. Aan de hand van een interactief verhaal geeft Guy Verlinde met zijn muzikanten een overzicht van de belangrijkste stromingen, invloedrijkste muzikanten, populairste instrumenten en schetst daarnaast ook de sociaal-culturele achtergrond met verhalen en anekdotes. Alles wordt geïllustreerd met veel livemuziek. Ze waren reeds eerder te gast en het was telkens een immens feest!', NULL, NULL, NULL, N'0ddd62b1-d85e-4d53-9363-8312449afcfe')
GO
