INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'd44e1a0f-645b-d208-9f3a-000000002481', N'Wim Opbrouck - Open hart', N'Wim Opbrouck staat bekend als acteur, zanger, presentator en goedlachse speelvogel. Maar minder mensen zijn vertrouwd met zijn beeldend werk.  Voor het eerst openbaart Opbrouck zijn werken in Museum Dr. Guislain, waar hij zijn tekeningen en schetsboeken vol bedenkingen en hersenspinsels op een groot publiek loslaat. Zijn collectie beslaat een periode van dertig jaar. De expo biedt een inzicht in het brein van Opbrouck die zich persoonlijk en kwetsbaar opstelt, maar verliest daarbij niet zijn gekende speelsheid en gulheid om de kijker te betrekken. Opbrouck wil de mens tot tekenen aanzetten, want in een tekening schuilt er kracht. Kracht om te ontdekken, maar ook om te ontsnappen.', CAST(10 AS Decimal(2, 0)), CAST(N'2022-07-20T06:00:00.000' AS DateTime), CAST(N'2022-07-21T05:59:59.000' AS DateTime), N'c8a75f81-b762-4a14-844f-638689c61bdd')
