INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'da44b351-3f5a-4464-8a92-13374c58e600', N'Sterren op de Koersvloer -  een Komische Kijk op de Koers', N'Karel Declercq bekijkt de wielerrage als wielervader. «Twee renners in het gezin hebben mijn leven sterk veranderd. Toen ik vroeger door het land reed dacht ik: hier ligt een zaaltje en daar een CC, nu is het: hier ligt een arrivé of een kasseistrook waar ik bidons stond te geven.»  Karel persifleert grote en kleine wielersterren die hij kent vanaf de jeugd. Met zang en komische beelden verwerkt hij het wielrennen tot een ambiancevol geheel.  De voorstelling vindt plaats in open lucht, op het gezellige binnenplein van het François Laurentinstituut. Het plein is deels overdekt.', NULL, NULL, NULL, NULL)
