INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'db6c270f-970a-4adf-a328-6be0421ad9d1', N'Room 13 Orchestra en Kapinga Gysel ''Bach to black''', N'Op 23/7/2011 stierf Amy Winehouse. 27 jaar, een leeftijd waarop wel meer rocksterren de legende ingaan. 10 jaar later, in 2021 krijgt ze een eerbetoon van het Room13 Orchestra. Haar legendarische plaat ‘Back to Black’ wordt op bijzondere wijze terug tot leven gewekt in ‘Bach to Black’. Kapinga Gysel, gezegend met een buitenaards mooie stem, doet de nummers alle eer aan.  Componist en pianist Yves Meerschaert herschrijft alle nummers op maat van het orkest. Onder leiding van dirigent Frank Van Troyen brengen 14 strijkers, 4 koperblazers, 2 houtblazers, 2 slagwerkers en Yves op piano hulde aan deze indrukwekkende zangeres. Kapinga Gysel (Zita Swoon) kruipt onder het vel van Amy. Lise Vanlerberghe zorgt voor passende beelden.', NULL, NULL, NULL, NULL)
