INSERT [dbo].[Evenementen] ([Identifier], [Naam], [Beschrijving], [Prijs], [StartDatum], [EindDatum], [ParentEvenementId]) VALUES (N'de20aed0-ac76-2f45-0bc5-000000003285', N'Protestantse Wandeling met Christine Liagre', N'Het Sint-Elisabethbegijnhof en de Holy Corner Begijnhoven vormden vaak een stad in de stad, dikwijls afgesloten met een poort van monumentale allure. Tijdens de wandeling door het Gentse Sint Elisabethbegijhof horen we de verhalen over moedige begijnen en over proveniersters en hun leven in het begijnhof. Een begijnhof kon ook verhuizen!  Vandaag krijgen de gebouwen die deel uitmaakten van een begijnhof vaak een heel nieuwe invulling. Zo ook de vroegere Sint-Elisabeth begijnhofkerk. Onttrokken aan de rooms-katholieke eredienst wordt deze kerk nu gebruikt door de anglicaanse Saint-Johns. Samen met de orthodoxe communauteit Heilige Apostel Andreas en de protestantse Rabotkerk vormen ze de zgn. Holy Corner. Goed geïntegreerd en geaccepteerd in de plaatselijke buurtgemeenschap organiseredeze Christelijke kerken van verschillende denominaties samen met enkele rooms-katholieken een aantal jaarlijks weerkerende activiteiten “over de kerkmuren heen”. Reserveren: christineliagre@hotmail.com', CAST(5 AS Decimal(2, 0)), CAST(N'2022-07-19T10:00:00.000' AS DateTime), CAST(N'2022-07-19T12:00:00.000' AS DateTime), N'425e0c2f-8d6a-449e-864f-900d58078e60')
